func TestWebAuthNValidateNewUserHandler(t *testing.T) {
	w := WebAuthn{"localhost", "http://localhost:8091"}
	ts := httptest.NewServer(w.NewUserHandler())
	defer ts.Close()

	const body = `{"authenticatorAttachment":"cross-platform","id":"K-tYJlDMN27O7mpfPpKIJAXhXtwN6T3YS-qlV7lld8u_Fl73AIaGaRS0s880erEAYiz_EnsSc_wG4eahZx77Ew","rawId":"K+tYJlDMN27O7mpfPpKIJAXhXtwN6T3YS+qlV7lld8u/Fl73AIaGaRS0s880erEAYiz/EnsSc/wG4eahZx77Ew==","type":"public-key","response":{"attestationObject":"","clientDataJSON":"eyJ0eXBlIjoid2ViYXV0aG4uY3JlYXRlIiwiY2hhbGxlbmdlIjoiVXYzOEJ5R0NaVThXUDE4UG1tSWRjZyIsIm9yaWdpbiI6Imh0dHA6Ly9sb2NhbGhvc3Q6ODA5MSIsImNyb3NzT3JpZ2luIjpmYWxzZX0="}}`
	r, err := http.Post(ts.URL, "application/json", strings.NewReader(body))
	if err != nil {
		t.Error(err)
	}
	if r.StatusCode != 200 {
		body, _ := io.ReadAll(r.Body)
		t.Errorf("wanted r.StatusCode == 200 got %d\nHTTP Response: %s", r.StatusCode, body)
	}

	var output map[string]interface{}
	dec := json.NewDecoder(r.Body)
	if err = dec.Decode(&output); err != nil && err != io.EOF {
		t.Error(err)
	}
	r.Body.Close()

	if len(output) != 0 {
		t.Errorf("wanted {}, got: %v", output)
	}
}

func mustDecodeHex(t *testing.T, s string) []byte {
	t.Helper()
	h, err := hex.DecodeString(s)
	if err != nil {
		panic(err)
	}
	return h
}

func TestDecodeAttestation(t *testing.T) {
	tests := map[string]struct {
		cbor string
		att  *attestation
	}{
		"attestation=none": {
			cbor: "",
			att: &attestation{
				Fmt:      "none",
				AuthData: mustDecodeHex(t, ""),
				AttStmt:  map[string]interface{}{},
			},
		},
		"attestation=direct": {
			cbor: "",
			att: &attestation{
				Fmt:      "packed",
				AuthData: mustDecodeHex(t, ""),
				AttStmt: map[string]interface{}{
					"alg": -7,
					"sig": mustDecodeHex(t, "304502206E0ECB7805C302E90E3DCEEE97328FE784531E88BF33922E4F570CA0462692FB022100BFF635CC1CD3F0E0A562839AD7AD92383F81C0B7F04579882E8885BE4FC70486"),
					"x5c": []interface{}{
						mustDecodeHex(t, ""),
					},
				},
			},
		},
	}
	for name, tc := range tests {
		a, err := decodeAttestation(mustDecodeHex(t, tc.cbor))
		if err != nil {
			t.Errorf("%s: failed to decode CBOR: %v", name, err)
		}
		if diff := cmp.Diff(tc.att, a); diff != "" {
			t.Errorf("%s: mismatch (-want +got):\n%s", name, diff)
		}
	}
}

func TestDecodeCBOR(t *testing.T) {
	tests := map[string]struct {
		hexBytes string
	}{
		"attestation=none":   {hexBytes: ""},
		"attestation=direct": {hexBytes: ""},
	}
	for name, tc := range tests {
		cbor, err := hex.DecodeString(tc.hexBytes)
		if err != nil {
			t.Errorf("%s: failed to decode hex: %v", name, err)
		}
		_, _, err = decodeCBOR(cbor)
		if err != nil {
			t.Errorf("%s: failed to decode CBOR: %v", name, err)
		}
	}
}
